INSERT INTO `wp_posts` VALUES (133,1,'2018-11-15 17:13:08','2018-11-15 09:13:08','<h1>C语言布尔bool实现方式-宏</h1>\n\n<pre><code class=\"\"><br />\\#include &lt;stdlib.h&gt;  \n#define BOOL int    // C语言 巧妙的使用 宏来解决C语言不支持 BOOL类型的问题   \n#define TRUE 1     // C语言 巧妙的使用 宏来解决C语言不支持 TRUE类型的问题   \n#define FALSE 0   // C语言 巧妙的使用 宏来解决C语言不支持 FALSE类型的问题   \n\nstruct array  \n{  \n    int count;  \n    int size;  \n    char *pBase;  \n};  \nvoid init_arr (struct array *pArr,int number);  \nvoid show_arr (const struct array *pArr);  \nBOOL is_empty (const struct array *pArr);  \n\nint main (void)  \n{  \n    struct array arr;  \n\n    init_arr (&amp;arr,10);  \n    show_arr (&amp;arr);  \n\n    return 0;     \n}  \nvoid init_arr (struct array *pArr,int number)  \n{  \n    pArr-&gt;pBase = (char *)malloc(sizeof(char)*number);  \n    if (NULL == pArr-&gt;pBase)  \n    {  \n        printf (\"Memory allocation failed!\\a\\n\");  \n        exit(EXIT_FAILURE);  \n    }  \n    else  \n    {  \n        pArr-&gt;size = number;  \n        pArr-&gt;count = 0;  \n    }  \n\n    return;  \n}  \nvoid show_arr (const struct array *pArr)  \n{  \n    int i;  \n    if ( is_empty(pArr) )  \n        printf (\"Array is empty!\\a\\n\");  \n    else  \n    {  \n        for (i=0;i&lt;(pArr-&gt;count);i++)  \n            printf (\"%c \",pArr-&gt;pBase[i]);  \n        printf (\"\\n\");  \n    }  \n\n    return;  \n}  \nBOOL is_empty (const struct array *pArr)  \n{  \n    if (pArr-&gt;count == 0)  \n        return TRUE;  \n    else  \n        return FALSE;  \n}  \n\n\n</code></pre>\n\n<h1>C语言执行CMD命令</h1>\n\n<img src=\"http://host810096584.s248.pppf.com.cn/wp-content/zimage/program/c/c_cmd.jpg\">\n\n<pre><code class=\"\"><br />//C语言  CMD命令集合\n#include &lt;stdio.h&gt;\n#include &lt;windows.h&gt;\n#include &lt;stdlib.h&gt;\n#define MAX_COUNT  18\n\nvoid show()\n{\n    int n,z = 1;\n    do\n    {\n        //***********显示提示信息**********\n        printf(\"   ********************************************CMD窗口命令集合*********************************************\\n\");\n        printf(\"   *                                                                                                       *\\n\");\n        printf(\"   * 1.appwiz.cpl   [卸载程序]               7.explorer     [文件管理]           13.Netplwiz     [用户账户]*\\n\");           //显示序号1的功能\n        printf(\"   * 2.compmgmt.msc [计算机管理]             8.Firewall.cpl [防火墙]             14.notepad      [记事本]  *\\n\");          //显示序号1的功能\n        printf(\"   * 3.comexp.msc   [系统服务]               9.magnify      [放大镜]             15.osk          [屏幕键盘]*\\n\");          //显示序号1的功能\n        printf(\"   * 4.control      [控制面板]               10.msinfo32    [系统信息]           16.powercfg.cpl [电源选项]*\\n\");           //显示序号1的功能\n        printf(\"   * 5.devmgmt.msc  [设备管理]               11.mstsc       [远程桌面]           17.regedt32     [注册表]  *\\n\");          //显示序号1的功能\n        printf(\"   * 6.diskmgmt.msc [磁盘管理]               12.ncpa.cpl    [网络适配器]         18.snippingtool [系统截图]*\\n\");            //显示序号1的功能\n        printf(\"   *                                                                                                       *\\n\");\n        printf(\"   *                                           0.退出系统                                                  *\\n\");           //显示序号0的功能\n        printf(\"   *                                                                                                       *\\n\");\n        printf(\"   *********************************************************************************************************\\n\");\n        printf(\"   *                                                                                                       *\\n\");\n        printf(\"   * 作者：zukgit   制作时间：20xx.xx  制作用途：  use-self                                                *\\n\");            //显示关于作者、制作时间以及制作用途的信息\n        printf(\"   *                                                                                                       *\\n\");\n        printf(\"   *********************************************************************************************************\\n\");\n\n        //********************************\n\n        printf(\"   请输入功能序号: \");             //提示用户输入\n\n        while(!scanf(\"%d\",&amp;n))                      //接受用户输入的功能序号，并且进行排错\n        {\n            printf(\"   输入序号有误,请重新输入: \");\n            fflush(stdin);\n        }\n        printf(\"   输您输入的是：%d\\n\",n);\n\n        while((n &lt; 0 || n &gt; MAX_COUNT))                                     //对用户输入不能实现的功能序号进行处理\n        {\n            printf(\"   抱歉,没有此功能,请重新输入功能序号: \");      //提示用户所输入的功能序号系统不能进行处理\n            fflush(stdin);\n            while(!scanf(\"%d\",&amp;n))                                  //接收用户重新输入的功能序号\n            {\n                printf(\"   输入序号有误,请重新输入: \");\n                fflush(stdin);\n            }\n            printf(\"\\n\");                                           //输出回车\n        }\n\n        switch(n)                           //对用户信息进行功能函数的调用\n        {\n            case 0:\n            {\n                printf(\"您将在3秒后退出\");\n                Sleep(3000);\n                z = 0;\n                break;\n            }\n            case 1:\n            {\n                system(\"appwiz.cpl\");\n                break;\n            }\n            case 2:\n            {\n                system(\"compmgmt.msc\");\n                break;\n            }\n            case 3:\n            {\n                system(\"comexp.msc\");\n                break;\n            }\n            case 4:\n            {\n                system(\"control\");\n                break;\n            }\n            case 5:\n            {\n                system(\"devmgmt.msc\");\n                break;\n            }\n            case 6:\n            {\n                system(\"diskmgmt.msc\");\n                break;\n            }\n            case 7:\n            {\n                system(\"explorer\");\n                break;\n            }\n            case 8:\n            {\n                system(\"Firewall.cpl\");\n                break;\n            }\n            case 9:\n            {\n                system(\"magnify\");\n                break;\n            }\n            case 10:\n            {\n                system(\"msinfo32\");\n                break;\n            }\n            case 11:\n            {\n                system(\"mstsc\");\n                break;\n            }\n            case 12:\n            {\n                system(\"ncpa.cpl\");\n                break;\n            }\n            case 13:\n            {\n                system(\"Netplwiz\");\n                break;\n            }\n            case 14:\n            {\n                system(\"notepad\");\n                break;\n            }\n            case 15:\n            {\n                system(\"osk\");\n                break;\n            }\n            case 16:\n            {\n                system(\"powercfg.cpl\");\n                break;\n            }\n            case 17:\n            {\n                system(\"regedt32\");\n                break;\n            }   \n            case 18:\n            {\n                system(\"snippingtool\");\n                break;\n            }                                                                                                                                                                                                                           \n        }\n\n    }while(z == 1);                             //输出回车\n\n}\n\nint main()  \n{\n    show();     //调用显示功能函数\n    return 0;\n}\n\n\n</code></pre>','C语言Demo集合','','publish','open','open','','c%e8%af%ad%e8%a8%80demo%e9%9b%86%e5%90%88','','','2018-11-15 17:15:27','2018-11-15 09:15:27','\r\n# C语言布尔bool实现方式-宏\r\n\r\n```\r\n\r\n#include &lt;stdlib.h&gt;  \r\n#define BOOL int    // C语言 巧妙的使用 宏来解决C语言不支持 BOOL类型的问题   \r\n#define TRUE 1     // C语言 巧妙的使用 宏来解决C语言不支持 TRUE类型的问