\n        printf(&quot;   *********************************************************************************************************\\n&quot;);\r\n        \r\n        //********************************\r\n        \r\n        printf(&quot;   请输入功能序号: &quot;);				//提示用户输入\r\n        \r\n        while(!scanf(&quot;%d&quot;,&amp;n))					    //接受用户输入的功能序号，并且进行排错\r\n        {\r\n            printf(&quot;   输入序号有误,请重新输入: &quot;);\r\n            fflush(stdin);\r\n        }\r\n        printf(&quot;   输您输入的是：%d\\n&quot;,n);\r\n        \r\n        while((n &lt; 0 || n &gt; MAX_COUNT))										//对用户输入不能实现的功能序号进行处理\r\n        {\r\n            printf(&quot;   抱歉,没有此功能,请重新输入功能序号: &quot;);		//提示用户所输入的功能序号系统不能进行处理\r\n            fflush(stdin);\r\n            while(!scanf(&quot;%d&quot;,&amp;n))									//接收用户重新输入的功能序号\r\n            {\r\n                printf(&quot;   输入序号有误,请重新输入: &quot;);\r\n                fflush(stdin);\r\n            }\r\n            printf(&quot;\\n&quot;);											//输出回车\r\n        }\r\n        \r\n        switch(n)							//对用户信息进行功能函数的调用\r\n        {\r\n            case 0:\r\n            {\r\n                printf(&quot;您将在3秒后退出&quot;);\r\n                Sleep(3000);\r\n                z = 0;\r\n                break;\r\n            }\r\n            case 1:\r\n            {\r\n                system(&quot;appwiz.cpl&quot;);\r\n                break;\r\n            }\r\n            case 2:\r\n            {\r\n                system(&quot;compmgmt.msc&quot;);\r\n                break;\r\n            }\r\n            case 3:\r\n            {\r\n                system(&quot;comexp.msc&quot;);\r\n                break;\r\n            }\r\n            case 4:\r\n            {\r\n                system(&quot;control&quot;);\r\n                break;\r\n            }\r\n            case 5:\r\n            {\r\n                system(&quot;devmgmt.msc&quot;);\r\n                break;\r\n            }\r\n            case 6:\r\n            {\r\n                system(&quot;diskmgmt.msc&quot;);\r\n                break;\r\n            }\r\n            case 7:\r\n            {\r\n                system(&quot;explorer&quot;);\r\n                break;\r\n            }\r\n            case 8:\r\n            {\r\n                system(&quot;Firewall.cpl&quot;);\r\n                break;\r\n            }\r\n            case 9:\r\n            {\r\n                system(&quot;magnify&quot;);\r\n                break;\r\n            }\r\n            case 10:\r\n            {\r\n                system(&quot;msinfo32&quot;);\r\n                break;\r\n            }\r\n            case 11:\r\n            {\r\n                system(&quot;mstsc&quot;);\r\n                break;\r\n            }\r\n            case 12:\r\n            {\r\n                system(&quot;ncpa.cpl&quot;);\r\n                break;\r\n            }\r\n            case 13:\r\n            {\r\n                system(&quot;Netplwiz&quot;);\r\n                break;\r\n            }\r\n            case 14:\r\n            {\r\n                system(&quot;notepad&quot;);\r\n                break;\r\n            }\r\n            case 15:\r\n            {\r\n                system(&quot;osk&quot;);\r\n                break;\r\n            }\r\n            case 16:\r\n            {\r\n                system(&quot;powercfg.cpl&quot;);\r\n                break;\r\n            }\r\n            case 17:\r\n            {\r\n                system(&quot;regedt32&quot;);\r\n                break;\r\n            }	\r\n            case 18:\r\n            {\r\n                system(&quot;snippingtool&quot;);\r\n                break;\r\n            }																																																							\r\n        }\r\n        \r\n    }while(z == 1);								//输出回车\r\n    \r\n}\r\n\r\nint main()  \r\n{\r\n    show();		//调用显示功能函数\r\n    return 0;\r\n}\r\n\r\n\r\n```',0,'http://host810096584.s248.pppf.com.cn/?p=133',0,'post','',0);
